import { Button } from 'semantic-ui-react';
import { leave, accessParticipants } from './Zoom'
import { liveParticipants } from '../data/liveParticipants'
import { useRecoilState } from 'recoil'
import { useRef, useEffect, useState } from 'react'

export const Head = () => {
    const [liveP, setLiveP] = useRecoilState(liveParticipants);
    const [hideSpeakerView, setHideSpeakerView] = useState(false);
    const [gridViewToggle, setGridViewToggle] = useState(false);

    useEffect(() => {
        // Update the document title using the browser API
        console.log('change in liveP')
    }, [liveP]);

    function HeartBeat() {
        useInterval(() => {
            // Your custom logic here
            const ap = accessParticipants()
            setLiveP(ap);
            if(!hideSpeakerView && ap?.length>0){
                setHideSpeakerView(true)
                hideSpeaker()
            }

        }, 5000);
        return ('')
    }

    function useInterval(callback, delay) {
        const savedCallback = useRef();
        // Remember the latest function.
        useEffect(() => {
            savedCallback.current = callback;
        }, [callback]);

        // Set up the interval.
        useEffect(() => {
            function tick() {
                savedCallback.current();
            }
            if (delay !== null) {
                let id = setInterval(tick, delay);
                return () => clearInterval(id);
            }
        }, [delay]);
    }

    const zoomMeeting = document.getElementById("zmmtg-root")
    const turnItOff = (e) => {
        e.preventDefault()
        zoomMeeting.style.display = 'none'
        zoomMeeting.style.height = '0px'
        zoomMeeting.style.width = '0px'
        zoomMeeting.style.position = 'relative'
        zoomMeeting.style.backgroundColor = 'black'
        zoomMeeting.style.zIndex = '1'
    }
    const turnItOn = (e) => {
        e.preventDefault()
        zoomMeeting.style.marginTop = '50px'
        zoomMeeting.style.display = 'flex'
        zoomMeeting.style.height = '90%'
        zoomMeeting.style.width = '100%'
        zoomMeeting.style.position = 'fixed'
        zoomMeeting.style.zIndex = '1'
        zoomMeeting.style.backgroundColor = 'black'
    }

    const hideSpeaker = () => {
        const event = new MouseEvent('mouseover', {
            view: window,
            bubbles: true,
            cancelable: true
        });
        const cb = document.querySelector("#wc-container-left > div:nth-child(3) > div > div.speaker-bar-container__horizontal-view-wrap > div:nth-child(1) > div > div")
        const cancelled = !cb.dispatchEvent(event);

        if (cancelled) {
            // A handler called preventDefault.
            alert("cancelled");
        } else {
            // None of the handlers called preventDefault.
            console.log(2)
            const hideSelfViewButton = document.querySelector("#wc-container-left > div:nth-child(3) > div > div.speaker-bar-container__horizontal-view-wrap > div:nth-child(1) > div > div:nth-child(3) > div > div > ul > li:nth-child(8) > a")
            console.log(hideSelfViewButton)
            hideSelfViewButton.click()
        }
    }

    const gallery = () => {
        let galleryButton = document.querySelector("#wc-container-left > div.full-screen-icon > div > ul > li:nth-child(2) > a")
        galleryButton.click()
        let participantsButton = document.querySelector("#wc-footer > div > div:nth-child(2) > button:nth-child(2)")
        participantsButton.click()
    }

    function simulateMouseover() {
        console.log('herererere', document.querySelector("#participants-list-0"))
        const event = new MouseEvent('mouseover', {
            view: window,
            bubbles: true,
            cancelable: true
        });
        const cb = document.querySelector("#participants-list-0");
        const cancelled = !cb.dispatchEvent(event);

        if (cancelled) {
            // A handler called preventDefault.
            alert("cancelled");
        } else {
            // None of the handlers called preventDefault.

        }
    }

    const spotlight = (index) => {

        console.log('Sim Mouse El:', document.getElementById("participants-list-" + index), "participants-list-" + index)
        const event = new MouseEvent('mouseover', {
            view: window,
            bubbles: true,
            cancelable: true
        });
        const cb = document.getElementById("participants-list-" + index);
        const cancelled = !cb.dispatchEvent(event);

        if (cancelled) {
            // A handler called preventDefault.
            alert("cancelled");
        } else {
            // None of the handlers called preventDefault.

        }
    }


    const gridView = () => {
        if (!gridViewToggle) {
            setGridViewToggle(true)
            console.log('happen')
            const event = new MouseEvent('mouseover', {
                view: window,
                bubbles: true,
                cancelable: true
            });
            const cb = document.querySelector("#wc-container-left > div:nth-child(3) > div > div.speaker-bar-container__horizontal-view-wrap > div:nth-child(1) > div > div")
            const cancelled = !cb.dispatchEvent(event);

            if (cancelled) {
                // A handler called preventDefault.
                alert("cancelled");
            } else {
                // None of the handlers called preventDefault.
                console.log(2)
                const hideSelfViewButton = document.querySelector("#wc-container-left > div:nth-child(3) > div > div.speaker-bar-container__horizontal-view-wrap > div:nth-child(1) > div > div:nth-child(3) > div > div > ul > li:nth-child(8) > a")
                console.log(hideSelfViewButton)
                hideSelfViewButton.click()
            }
            let galleryButton = document.querySelector("#wc-container-left > div.full-screen-icon > div > ul > li:nth-child(2) > a")
            galleryButton.click()

        }
    }


    return (
        <header className='Header'>
            <HeartBeat></HeartBeat>


            <div className='left'>

                <Button onClick={turnItOff}>
                    Hide Zoom Window
                </Button>

                <Button onClick={turnItOn}>
                    Show Zoom Window
                </Button>

                <Button color='red' onClick={() => leave()}>
                    Leave Meeting
                </Button>

                <Button color='green' onClick={() => gallery()}>
                    gallery
                </Button>

                <Button color='green' onClick={() => simulateMouseover()}>
                    simulate hover
                </Button>

                <Button color='green' onClick={() => spotlight()}>
                    spotlight (Only works with video)
                </Button>

                <Button color='olive' onClick={() => gridView()}>
                    Show Grid View
                </Button>
            </div>
            <div className='center'>         <h1 >Zoom App</h1>
            </div>
        </header>
    );
};
