import './App.css';
import { Button, Form } from 'semantic-ui-react'
import React, { useState, useEffect } from 'react';

declare var ZoomMtg

ZoomMtg.setZoomJSLib('https://source.zoom.us/1.8.6/lib', '/av');

ZoomMtg.preLoadWasm();
ZoomMtg.prepareJssdk();

function App() {
  const [settings, setSettings] = useState({ 'meetingNumber': '', 'password': '', 'userName': '' });
  useEffect(() => {
    // Update the document title using the browser API
    console.log(settings)  });
  // setup your signature endpoint here: https://github.com/zoom/websdk-sample-signature-node.js
  var signatureEndpoint = 'http://localhost:4000'
  var apiKey = 'IEocUyyPQXCyN1Mnn6ztzg'
  var meetingNumber = '78216010287'
  var role = 0
  var leaveUrl = 'http://localhost:3000'
  var userName = 'React'
  var userEmail = ''
  var passWord = 'H7vQXg'

  function getSignature(e) {
    e.preventDefault();

    fetch(signatureEndpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        meetingNumber: meetingNumber,
        role: role
      })
    }).then(res => res.json())
      .then(response => {
        startMeeting(response.signature)
      }).catch(error => {
        console.error(error)
      })
  }

  function startMeeting(signature) {
    document.getElementById('zmmtg-root').style.display = 'block'

    ZoomMtg.init({
      leaveUrl: leaveUrl,
      isSupportAV: true,
      success: (success) => {
        console.log(success)

        ZoomMtg.join({
          signature: signature,
          meetingNumber: settings.meetingNumber,
          userName: settings.userName,
          apiKey: apiKey,
          userEmail: userEmail,
          passWord: settings.password,
          success: (success) => {
            console.log(success)
          },
          error: (error) => {
            console.log(error)
          }
        })

      },
      error: (error) => {
        console.log(error)
      }
    })
  }
  const handleChange = (e, { name, value }) => {
    setSettings({...settings, [name]: value })
  }

  return (
    <div className="App">
      <main>
        <h1>Sky Video Conferencing</h1>
        <Form onSubmit={getSignature}>
          <Form.Group>
            <Form.Input
              placeholder='Meeting Number'
              name='meetingNumber'
              onChange={handleChange}
            />
            <Form.Input
              placeholder='Meeting Password'
              name='password'
              onChange={handleChange}
            />
            <Form.Input
              placeholder='Username'
              name='userName'
              onChange={handleChange}
            />
            <Form.Button content='Submit' />
          </Form.Group>
        </Form>
        {/*<button onClick={getSignature}>Join Zoom Meeting</button>*/}

      </main>
    </div>
  );
}

export default App;
