import { Form } from 'semantic-ui-react'
import React, { useState, useEffect } from 'react';
import { useRecoilState } from 'recoil'
import { liveParticipants } from '../data/liveParticipants'
import { Button } from 'semantic-ui-react'

declare var ZoomMtg

ZoomMtg.setZoomJSLib('https://source.zoom.us/1.8.6/lib', '/av');

ZoomMtg.preLoadWasm();
ZoomMtg.prepareJssdk();

var meetingNumber = ''
var password = ''
var userName = ''
var signatureEndpoint = 'http://localhost:4000'
var apiKey = 'IEocUyyPQXCyN1Mnn6ztzg'
var role = 0
var leaveUrl = 'http://localhost:3000'
var userEmail = ''

export const muteIndiv = (id, bool) => {
    console.log('Muting', id)
    ZoomMtg.mute({
        userId: id,
        mute: bool
    });
}

export const Zoom = () => {
    const [, setLiveP] = useRecoilState(liveParticipants);
    const [settings, setSettings] = useState({ 'meetingNumber': '', 'password': '', 'userName': '' });
    const [meetingStatus, setMeetingStatus] = useState(false);
    const [participant, setParticipant] = useState([]);
    const zoomMeeting = document.getElementById("zmmtg-root")
    const turnItOn = (e) => {
        e.preventDefault()
        zoomMeeting.style.marginTop = '200px'
        zoomMeeting.style.display = 'flex'
        zoomMeeting.style.height = '90%'
        zoomMeeting.style.width = '100%'
        zoomMeeting.style.position = 'fixed'
        zoomMeeting.style.zIndex = '1'
        zoomMeeting.style.backgroundColor = 'black'
    }

    const turnItOff = (e) => {
        e.preventDefault()
        zoomMeeting.style.display = 'none'
        zoomMeeting.style.height = '0px'
        zoomMeeting.style.width = '0px'
        zoomMeeting.style.position = 'relative'
        zoomMeeting.style.backgroundColor = 'black'
        zoomMeeting.style.zIndex = '1'
    }
    useEffect(() => {
        meetingNumber = settings.meetingNumber
        password = settings.password
        userName = settings.userName
    }, [settings]
    );

    function getSignature(e) {
        e.preventDefault();

        fetch(signatureEndpoint, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                meetingNumber: meetingNumber,
                role: role
            })
        }).then(res => res.json())
            .then(response => {
                startMeeting(response.signature)
            }).catch(error => {
                console.error(error)
            })
    }

    function startMeeting(signature) {
        document.getElementById('zmmtg-root').style.display = 'block'

        ZoomMtg.init({
            leaveUrl: leaveUrl,
            isSupportAV: true,
            success: (success) => {
                console.log(success)

                ZoomMtg.join({
                    signature: signature,
                    meetingNumber: meetingNumber,
                    userName: userName,
                    apiKey: apiKey,
                    userEmail: userEmail,
                    passWord: password,
                    success: (success) => {
                        console.log(success)
                        setMeetingStatus(true)
                    },

                    error: (error) => {
                        console.log(error)
                    }
                })
            },
            error: (error) => {
                console.log(error)
            }

        })
    }
    const handleChange = (e, { name, value }) => {
        setSettings({ ...settings, [name]: value })
    }

    const accessParticipants = () => {
        ZoomMtg.getAttendeeslist({
            success: function (res) {
                setParticipant(res.result.attendeesList)
                setLiveP(res.result.attendeesList)
                console.log('access', res)
            }
        })
    }

    const muteAll = () => {
        console.log('Mute all')
        ZoomMtg.muteAll({
            muteAll: true
        });
    }



    const rename = (id, oldname, newname) => {
        ZoomMtg.rename({
            userId: id,
            oldName: oldname,
            newName: newname
        });
    }


    return (
        <div className="App">
            <main>
                <h1>Sky Video Conferencing</h1>
                <Form onSubmit={getSignature}>
                    <Form.Group>
                        <Form.Input
                            placeholder='Meeting Number'
                            name='meetingNumber'
                            onChange={handleChange}
                        />
                        <Form.Input
                            placeholder='Meeting Password'
                            name='password'
                            onChange={handleChange}
                        />
                        <Form.Input
                            placeholder='Username'
                            name='userName'
                            onChange={handleChange}
                        />
                        <Form.Button content='Submit' />
                    </Form.Group>
                </Form>
                <Button onClick={accessParticipants}>
                    Update Participant
        </Button>
                <Button onClick={turnItOff}>
                    Hide Zoom Window
        </Button>
                <Button onClick={turnItOn}>
                    Show Zoom Window
        </Button>

            </main>
        </div>
    );
}
