import './App.css';
import { Form } from 'semantic-ui-react'
import React, { useState, useEffect } from 'react';

declare var ZoomMtg

ZoomMtg.setZoomJSLib('https://source.zoom.us/1.8.6/lib', '/av');

ZoomMtg.preLoadWasm();
ZoomMtg.prepareJssdk();

var meetingNumber = ''
var password = ''
var userName = ''
var signatureEndpoint = 'http://localhost:4000'
var apiKey = 'IEocUyyPQXCyN1Mnn6ztzg'
var role = 1
var leaveUrl = 'http://localhost:3000'
var userEmail = ''

function App() {
  const [settings, setSettings] = useState({ 'meetingNumber': '', 'password': '', 'userName': '' });
  const [meetingStatus, setMeetingStatus] = useState(false);
  const [participant, setParticipant] = useState([]);

  useEffect(() => {
    meetingNumber = settings.meetingNumber
    password = settings.password
    userName = settings.userName
  }, [settings]
  );

  function getSignature(e) {
    e.preventDefault();

    fetch(signatureEndpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        meetingNumber: meetingNumber,
        role: role
      })
    }).then(res => res.json())
      .then(response => {
        startMeeting(response.signature)
      }).catch(error => {
        console.error(error)
      })
  }

  function startMeeting(signature) {
    document.getElementById('zmmtg-root').style.display = 'block'

    ZoomMtg.init({
      leaveUrl: leaveUrl,
      isSupportAV: true,
      success: (success) => {
        console.log(success)

        ZoomMtg.join({
          signature: signature,
          meetingNumber: meetingNumber,
          userName: userName,
          apiKey: apiKey,
          userEmail: userEmail,
          passWord: password,
          success: (success) => {
            console.log(success)
            setMeetingStatus(true)
          },

          error: (error) => {
            console.log(error)
          }
        })
        ZoomMtg.getAttendeeslist({
          success: function (res) {
            setParticipant(res.result.attendeesList)
          }
        })
      },
      error: (error) => {
        console.log(error)
      }
    })
  }
  const handleChange = (e, { name, value }) => {
    setSettings({ ...settings, [name]: value })
  }

  /*const accessParticipants = () => {
    ZoomMtg.getAttendeeslist({
      success: function (res) {
        setParticipant(res.result.attendeesList)
      }
    })
  }*/

  const muteIndividual = (id) => {
    console.log(id)
    ZoomMtg.mute({
      userId: id,
      mute: true
    });
  }

  const muteAll = () => {
    console.log('Mute all')
    ZoomMtg.muteAll({
      muteAll: true
  });
  }
  const muteIndiv = (id) => {
    console.log('Muting',id)
    ZoomMtg.mute({
      userId: id,
      mute: true
    });
  //console.log(participant[1].userId, participant[0].userId)
  }
  return (
    <div className="App">
      <main>
        <h1>Sky Video Conferencing</h1>
        <Form onSubmit={getSignature}>
          <Form.Group>
            <Form.Input
              placeholder='Meeting Number'
              name='meetingNumber'
              onChange={handleChange}
            />
            <Form.Input
              placeholder='Meeting Password'
              name='password'
              onChange={handleChange}
            />
            <Form.Input
              placeholder='Username'
              name='userName'
              onChange={handleChange}
            />
            <Form.Button content='Submit' />
          </Form.Group>
        </Form>

        {participant.map((at) => 
        <h5>{at.userId} | {at.audio} | {at.isCoHost} | {at.isGuest} | {at.isHold} | {at.isHost} | {at.muted} | {at.userGUID} | {at.userId} | {at.userName}

        <Form onSubmit={muteIndiv(at.userId)}>
          <Form.Group>
            <Form.Button content={'mute:', at.userName} />
          </Form.Group>
        </Form>

        </h5>


        )
        }

        <Form onSubmit={ () => muteAll}>
          <Form.Group>
            <Form.Button content='Mute All' />
          </Form.Group>
        </Form>
      </main>
    </div>
  );
}

export default App;
