import "./participant.css";
import {  Icon } from 'semantic-ui-react'
import {muteIndiv} from './Zoom'

export const Participant = ({id, originalName, muted, isHost, editMode, newName, setNewName, saveNewName}) => {
    return(
        <div className='participant'>
            {
                (editMode) ?
                <div className='edit-form'>
                    <p><b>Original name: </b> {originalName}</p>
                    <input type='text' value={newName} onChange={(e) => setNewName(e.target.value)}/>
                    <button onClick={() => saveNewName()}>Save</button>
                </div>
                :
                <p className='name'><b>Original name: </b> {originalName}</p>
            }
            {
                (muted) ? <p className='muted'>Muted</p> : <p>Unmuted</p>
            }
            {
                (isHost) ? <p className='host'><b>Host</b></p> : <p>Guest</p>
            }
            <Icon
                name={muted ? 'mute' : 'unmute'} onClick={() => muteIndiv(id, muted ? false : true)}
                color={muted ? 'red' : 'green'} onClick={() => muteIndiv(id, muted ? false : true)}
            ></Icon>
        </div>
    )
}
